mapper.select:
SELECT * FROM inbox_question_answers WHERE parent_id = 0
AND content like #{content}
AND deleted_date IS NULL ORDER BY updated_date DESC , id DESC

SELECT count(qa.id) FROM inbox_question_answers qa WHERE qa.type=1 AND parent_id=0 AND deleted_date IS NULL
AND content like concat('%',#{search_str},'%')
AND qa.parent_id=#{questionId}

SELECT count(qa.id) FROM inbox_question_answers qa WHERE qa.type=2 AND qa.deleted_date IS NULL
AND qa.content like concat('%',#{search_str},'%')
AND qa.parent_id=#{questionId}

SELECT qa.* FROM inbox_question_answers qa
WHERE qa.parent_id=0
AND qa.deleted_date IS  NULL

    AND  qa.content like concat('%',#{search_str},'%')


    AND qa.parent_id=#{questionId}


    AND qa.parent_id=#{questionId}


    AND qa.question_by_userid=#{vendor_id}

ORDER BY updated_date DESC
limit #{start},#{limit}

SELECT qa.* FROM inbox_question_answers qa
WHERE qa.type=2
AND qa.deleted_date IS  NULL

   AND qa.content like concat('%',#{search_str},'%')


    AND qa.parent_id=#{questionId}

limit #{start},#{limit}

mapper.update:
update inbox_question_answers
set   content = #{content}
    ,question_by_userid = #{question_by_userid}
    ,type = #{type}
    ,parent_id = #{parent_id}
    ,deleted_date = #{deleted_date}
    ,updated_date = #{updated_date}
where id=#{id}

update inbox_question_answers
set votes=#{votes}
where id=#{questionItemId}

update inbox_question_answers
set   content = #{content}
,read_flag = 0
where id=#{id}

update inbox_question_answers
set  deleted_date = #{deleted_date}
where id=#{id}
