mapper.select:
select * from plans
WHERE plan_id=plan_id

    AND vendor_id=#{pager.vendor_id}


    AND plan_name like concat('%',#{pager.search_str},'%')

limit #{pager.start},#{pager.limit}

mapper.update:
update plans set
  plan_id = #{plan_id}
  , plan_name=#{plan_name}
  , frequency=#{frequency}
  , real_price=#{real_price}
  , vr_price=#{vr_price}
  , vendor_id = #{vendor_id}
where id =#{id}

mapper.select:
SELECT count(id) from plans WHERE plan_id = plan_id
AND vendor_id=#{vendor_id}
AND plan_name like concat('%',#{search_str},'%')

select plan.* from (select p.*, v.legal_name as vendor_name from plans p, vendors v where p.vendor_id = v.id) plan LEFT JOIN sub_manage s on plan.id = s.plan_id where (s.user_id != #{user_id} or s.user_id is null) AND plan.real_price > 0.00
AND plan.vendor_name like concat('%',#{search_str},'%')
ORDER BY plan.id ASC;
