mapper.select:
SELECT ROUND(SUM(stars)/COUNT(id),1) FROM customer_reviews
WHERE dataset_id=#{id}
AND deleted_date IS  NULL

mapper.update:
update customer_reviews
set helpful_num=#{helpfulNum}
where id=#{reviewItemId}

mapper.select:
SELECT cr.*,ds.name as dataset_name FROM customer_reviews cr, data_sets ds WHERE deleted_date IS NULL AND cr.dataset_id = ds.id
AND title like concat('%',#{search_str},'%')
AND ds.vendor_id=#{vendor_id}
ORDER BY updated_date DESC limit #{start},#{limit}

SELECT * from customer_reviews cr, data_sets ds
WHERE cr.deleted_date IS NULL
and ds.id=cr.dataset_id

    AND ds.vendor_id = #{vendor_id}


    AND title like concat('%',#{search_str},'%')

limit #{start},#{limit}

mapper.update:
update customer_reviews set
title = #{title}
, content = #{content}
, stars = #{stars}
, customer_id = #{customer_id}
, dataset_id = #{dataset_id}
, updated_date = #{updated_date}
, deleted_date = #{deleted_date}
 , additional_imgs = #{additional_imgs}
  , helpful_num = #{helpful_num}
where id =#{id}

mapper.select:
SELECT count(cr.id) from customer_reviews cr, data_sets ds WHERE cr.deleted_date IS NULL and ds.id=cr.dataset_id
AND title like concat('%',#{search_str},'%')
AND ds.vendor_id=#{vendor_id}

SELECT SUM(helpful_num) FROM customer_reviews cr, data_sets ds
WHERE ds.id=cr.dataset_id
AND ds.vendor_id = #{vendor_id}
