mapper.select:
select * from email_lists where
vendor_id = #{vendor_id}
AND couponOrEmail = 1

    and title like concat('%',#{search_str},'%')

limit #{start},#{limit}

mapper.insert:
insert into email_lists
( title
   , content
   , vendor_id
   , dataset_ids
   , email_address
   , email_nums
   , updated_at
   , couponOrEmail
    )
VALUES (#{title}
   , #{content}
   , #{vendor_id}
   , #{dataset_ids}
   , #{email_address}
   , #{email_nums}
   , NOW()
   , #{couponOrEmail})

    SELECT LAST_INSERT_ID() as id

mapper.update:
update email_lists set
  title = #{title}
   , content = #{content}
    , send_flag = #{send_flag}
   where id =#{id}

mapper.select:
select * from email_lists where
vendor_id = #{vendor_id}
AND couponOrEmail = 2

    and title like concat('%',#{search_str},'%')

limit #{start},#{limit}
